// 1. Controllo sicurezza se il payload Ã¨ vuoto
const arr = Array.isArray(msg.payload) ? msg.payload : [];

// 2. Filtra i valori validi
const values = arr.map(v => Number(v.ValoreDiametro)).filter(n => Number.isFinite(n) && n > 0);

// DEBUG: Scrive nella console di Node-RED quanti dati ha trovato
node.warn("Dati trovati per Gaussiana: " + values.length);

if (values.length === 0) {
  node.warn("Nessun dato trovato nelle ultime 2 ore per la ricetta corrente.");
  return null; // Esce se non ci sono dati
}

// 3. Parametri statistici
const step = 0.5; // Larghezza barre
const n = values.length;
const mean = values.reduce((a, b) => a + b, 0) / n;
const variance = values.reduce((a, b) => a + (b - mean) ** 2, 0) / n;
const sigma = Math.sqrt(variance) || 1; // Evita divisione per zero

// 4. Calcolo range (Minimo e Massimo)
const min = Math.min(...values);
const max = Math.max(...values);
// Allarghiamo un po' il grafico a sinistra e destra
const start = Math.floor(min / step) * step - step;
const end = Math.ceil(max / step) * step + step;

const labels = [];
const hist = [];

// 5. Inizializza i bucket dell'istogramma a 0
for (let x = start; x <= end + 0.001; x += step) {
  labels.push(x.toFixed(1)); // Etichetta asse X (es: "180.5")
  hist.push(0);
}

// 6. Riempie i bucket con i dati reali
for (const v of values) {
  // Trova l'indice corrispondente al valore
  const idx = Math.floor((v - start) / step);
  if (idx >= 0 && idx < hist.length) hist[idx]++;
}

// 7. Prepara i dati finali per il grafico
const chartData = [];

labels.forEach((l, i) => {
  const xVal = l;
  const xNum = parseFloat(l);

  // Calcolo Curva Gaussiana Teoretica
  let yGauss = (1 / (sigma * Math.sqrt(2 * Math.PI))) * Math.exp(-0.5 * ((xNum - mean) / sigma) ** 2);
  yGauss = yGauss * n * step; // Adatta l'altezza della curva all'istogramma

  // Serie 1: Istogramma (Barre)
  chartData.push({
    "series": "Reale",
    "x": xVal,
    "y": hist[i]
  });

  // Serie 2: Gaussiana (Curva)
  chartData.push({
    "series": "Ideale",
    "x": xVal,
    "y": Number(yGauss.toFixed(2))
  });
});

msg.payload = chartData;
return msg;

