SELECT 
    AVG(
        NULLIF(
            TRY_CAST(REPLACE(REPLACE(REPLACE(SingleAvgFeature2,'$',''),'#',''),',','.') AS FLOAT),
            0
        )
    ) * 2 AS MediaUltimo
FROM DataAnalysis
WHERE ReceiptName = '{{{global.ricetta}}}'
  AND DateTime >= DATEADD(MINUTE, -1, GETDATE());

