var lines = [];
var ricettaRaw = global.get("ricetta") || "Manuale";
var ricettaSafe = ricettaRaw.replace(/\s+/g, '_');

for (var i = 0; i < msg.payload.length; i++) {
    var row = msg.payload[i];

    if (row.ValoreDiametro == null) continue;

    var val = parseFloat(row.ValoreDiametro);
