    var timestamp = new Date(row.DateTime).getTime();

    lines.push({
        measurement: "controllo_qualita",
        tags: {
            ricetta: ricettaSafe,
            parametro: "diametro"
        },
        fields: {
            valore: val
        },
        timestamp: timestamp

    });
}

msg.payload = lines;
return msg;

var points = [];
var ricettaRaw = global.get("ricetta") || flow.get("ricetta") || "Manuale";
var ricettaSafe = String(ricettaRaw).replace(/\s+/g, "_");
for (var i = 0; i < msg.payload.length; i++) {
  var row = msg.payload[i];
  if (row.ValoreDiametro == null) continue;
  var val = parseFloat(row.ValoreDiametro);
  if (isNaN(val)) continue;
  var iso = (row.DateTime || "").toString().replace(" ", "T");
  var ts = Date.parse(iso);
  if (isNaN(ts)) ts = Date.now();
  points.push({ measurement: "controllo_qualita", tags: { ricetta: ricettaSafe, parametro: "diametro" }, fields: { valore: val }, timestamp: ts });
}
msg.payload = points;
return msg;

var ricettaRaw = global.get("ricetta") || flow.get("ricetta") || "Manuale";
var ricettaSafe = String(ricettaRaw).replace(/\s+/g, "_");

var lines = [];

for (var i = 0; i < msg.payload.length; i++) {
    var row = msg.payload[i];
    if (row.ValoreDiametro == null) continue;
    var val = parseFloat(row.ValoreDiametro);
    if (isNaN(val)) continue;

    var ts = new Date(row.DateTime).getTime();
    if (isNaN(ts)) ts = Date.now();

    // Formato Line Protocol di InfluxDB
    var line = "controllo_qualita,ricetta=" + ricettaSafe + ",parametro=diametro valore=" + val + " " + ts;
    lines.push(line);
}

msg.payload = lines;
return msg;

msg.payload = [
    "controllo_qualita,ricetta=TEST,parametro=diametro valore=123 1700000000000"
];
return msg;

// Crea l'array per il menu a tendina Dashboard 2.0
var options = msg.payload.map(function(row) {
    return {
        label: row.ReceiptName,
