let lines = msg.payload.map(r => {
    return `ricette name="${r.ReceiptName.replace(/"/g, '')}"`;
});

msg.payload = lines.join("\n");
return msg;

// Legge la ricetta dall'ultima riga prodotta oggi
if (!msg.payload || msg.payload.length === 0) {
    return null;
}

let ricetta = msg.payload[0].ReceiptName;
let ricettaCorrente = global.get('ricetta');

// Se Ã¨ cambiata, la aggiorno
if (ricetta && ricetta !== ricettaCorrente) {
    global.set('ricetta', ricetta);
    global.set('ricetta_auto', ricetta);   // utile anche per debug / Influx tag
    node.warn("AUTO: ricetta aggiornata â†’ " + ricetta);
}

// Non devo mandare niente in giro, i grafici si aggiornano giÃ  coi loro timer
return null;

var d = msg.payload[0] || {};

msg.payload = {
