var ricettaSafe = global.get("ricetta") || "Manuale";
ricettaSafe = String(ricettaSafe).replace(/\s+/g, "_");

let out = [];

for (let row of msg.payload) {

    if (row.ValoreDiametro === null || row.ValoreDiametro === undefined) continue;

    let val = parseFloat(row.ValoreDiametro);
    if (isNaN(val)) continue;

    let ts = new Date(row.DateTime).getTime();
    if (isNaN(ts)) continue;

    let lp = `controllo_qualita,ricetta=${ricettaSafe},parametro=diametro valore=${val} ${ts}`;

    out.push({ payload: lp });
}

node.status({ text: "Punti: " + out.length });
return [out];

let ricetta = global.get("ricetta") || "Manuale";
ricetta = String(ricetta).replace(/\s+/g, "_");

let rows = Array.isArray(msg.payload) ? [...msg.payload] : [];

// filtro valori validi
rows = rows.filter(r =>
    r.ValoreDiametro != null &&
    !isNaN(parseFloat(r.ValoreDiametro)) &&
    !isNaN(new Date(r.DateTime).getTime())
);

// sort sicuro
rows.sort((a, b) => {
    let ta = new Date(a.DateTime).getTime();
    let tb = new Date(b.DateTime).getTime();
    return ta - tb;
});

// rimozione duplicati
let unique = [];
let seen = new Set();

for (let r of rows) {
    let ts = new Date(r.DateTime).getTime();
    if (seen.has(ts)) continue;
    seen.add(ts);
    unique.push(r);
}

node.warn("Punti validi: " + unique.length);
let m = {};
for (let r of unique) {
    let ts = new Date(r.DateTime).getTime();
    m[ts] = (m[ts] || 0) + 1;
}

let duplicates = Object.entries(m).filter(([,count]) => count > 1);
node.warn("Duplicati: " + duplicates.length);

msg.payload = { ricetta, points: unique };
return msg;

const ricetta = msg.payload.ricetta;
const points = msg.payload.points;

let batches = [];
let batch = [];
let BATCH_SIZE = 5000;

for (let p of points) {
    let ts = new Date(p.DateTime).getTime();
    let v = parseFloat(p.ValoreDiametro);

    let line = `controllo_qualita,ricetta=${ricetta},parametro=diametro valore=${v} ${ts}`;
    batch.push(line);

    if (batch.length >= BATCH_SIZE) {
        batches.push(batch.join("\n"));
        batch = [];
    }
}

if (batch.length > 0) batches.push(batch.join("\n"));

node.warn("Batch creati: " + batches.length);

return [batches.map(b => ({ payload: b }))];

msg._originalPayload = msg.payload;
msg.retry = 0;
return msg;

let status = msg.statusCode || 0;

if (status === 204) {
    node.status({fill:"green", shape:"dot", text:"OK"});
    return msg;
}

if ([429, 500, 503].includes(status)) {
    let retry = msg.retry || 0;
    if (retry < 5) {
        node.warn(`Influx occupato â†’ retry #${retry + 1}`);
        return [{
            payload: msg._originalPayload,
            _originalPayload: msg._originalPayload,
            retry: retry + 1
        }];
    }
    node.error("Fallito dopo 5 tentativi!", msg);
    return null;
}

node.error("Errore Influx: " + status, msg);
return null;

