        .replace(/ /g, "\\ ")
        .replace(/,/g, "\\,")
        .replace(/=/g, "\\=");

    return `ricette,name=${name} value=1 ${fixedTimestamp}`;
});

msg.payload = lines.join("\n");
return msg;

